const loans = require('./loans');
const cp = require('./cp');

const overrides = [
  loans,
  cp,
];

const apply = (props, req) => {
  const newProps = overrides.reduce((currentProps, overrideProp) => {
    overrideProp.apply(currentProps, req);
    return currentProps;
  }, props);

  return newProps;
};

module.exports = {
  apply,
};
