/**
 * Get necessary properties from req object
 * Â¡Â¡Â¡Please do not pass entire req object!!!
 *
 * @param {object} req - Express request object
 * @param {object} res - Express response object
 * @returns {object}
 */
module.exports = ({ headers, cookies, platform }, res) => ({
  headers,
  cookies,
  platform,
  res: {
    header: (...params) => res.header(...params),
    get: (...params) => res.get(...params),
  },
});
