const {
  get,
  getValue,
  addUpdateHandler,
} = require('../index');

/* eslint-disable no-console */

const getConfigurations = () => {
  console.log('get global uiNavigationVersion', get('uiNavigationVersion'));

  console.log('get MP uiNavigationVersion', get('uiNavigationVersion', 'MP'));

  console.log('get MP MLC uiNavigationVersion', get('uiNavigationVersion', 'MP', 'MLC'));

  console.log('getValue global uiNavigationVersion', getValue({
    key: 'uiNavigationVersion',
  }));

  console.log('getValue MP uiNavigationVersion', getValue({
    key: 'uiNavigationVersion',
    platform: {
      id: 'MP',
    },
  }));

  console.log('getValue MP MLC uiNavigationVersion', getValue({
    key: 'uiNavigationVersion',
    platform: {
      id: 'MP',
      siteId: 'MLC',
    },
  }));
};

getConfigurations();

addUpdateHandler(() => {
  console.log('Configuration updated');
  getConfigurations();
});
