const detection = require('.');

describe('Layout Type Detection', () => {
  it('returns true when the layout is plus', () => {
    expect(detection.isPlus('full')).toEqual(true);
  });

  it('returns true when the layout is hidden', () => {
    expect(detection.isHidden('hidden')).toEqual(true);
  });

  it('returns false when the layout is plus', () => {
    expect(detection.isHidden('full')).toEqual(false);
  });

  it('returns true when the layout is clean', () => {
    expect(detection.isClean('clean')).toEqual(true);
  });

  it('returns false when the layout is plus', () => {
    expect(detection.isClean('full')).toEqual(false);
  });
});
