const React = require('react');
const ShallowRenderer = require('react-test-renderer/shallow');
const Css = require('.');

const defaultProps = {
  platformId: 'MP',
  siteId: 'MLA',
  navigationType: 'full',
  firstRequestInlineStyles: false,
  browser: {
    name: 'chrome',
    major: '80',
  },
  cdn: {
    link: 'https://some-cdn-link.com',
  },
  configGetter: () => 'some-key-value',
  configEnvironment: 'some-environment',
  addConfigUpdateHandler: () => {},
};

describe('Mercado Pago Navigation', () => {
  describe('Css component', () => {
    it('should match snapshot for default properties', () => {
      const renderer = new ShallowRenderer();
      const component = (
        <Css {...defaultProps} />
      );

      const renderedComponent = renderer.render(component);

      expect(renderedComponent).toMatchSnapshot();
    });

    it('should match snapshot with ie < 9', () => {
      const renderer = new ShallowRenderer();
      const component = (
        <Css
          {...defaultProps}
          browser={{
            name: 'ie',
            major: '8',
          }}
        />
      );

      const renderedComponent = renderer.render(component);

      expect(renderedComponent).toMatchSnapshot();
    });

    it('should match snapshot with ie equals 9', () => {
      const renderer = new ShallowRenderer();
      const component = (
        <Css
          {...defaultProps}
          browser={{
            name: 'ie',
            major: '9',
          }}
        />
      );

      const renderedComponent = renderer.render(component);

      expect(renderedComponent).toMatchSnapshot();
    });

    it('should match snapshot with firstRequestInlineStyles configuration', () => {
      const renderer = new ShallowRenderer();
      const component = (
        <Css
          {...defaultProps}
          firstRequestInlineStyles
        />
      );

      const renderedComponent = renderer.render(component);

      expect(renderedComponent).toMatchSnapshot();
    });
  });
});
