const { isHidden } = require('../../../../layout-type-detection');

const copyrightProps = ({ labels, type }) => {
  if (isHidden(type)) return {};

  return {
    year: new Date().getFullYear(),
    companyName: labels.companyName,
    extraCompanyName: labels.extraCompanyName,
  };
};

module.exports = copyrightProps;
