describe('Mercado Pago Navigation', () => {
  describe('Footer Navigation Component', () => {
    it('matches the default snapshot for footer navigation', () => {
      const renderer = new ShallowRenderer();
      const NavSections = { ...baseProps };

      const performance = <Navigation {...NavSections.performance} />;
      const navHeaderSections = (
        <Navigation {...NavSections.navHeaderSections} />
      );
      const navAboutSection = <Navigation {...NavSections.navAboutSection} />;

      expect(renderer.render(performance)).toMatchSnapshot();
      expect(renderer.render(navHeaderSections)).toMatchSnapshot();
      expect(renderer.render(navAboutSection)).toMatchSnapshot();
    });
  });
});
