const React = require('react');
const Image = require('frontend-image');
const PropTypes = require('prop-types');

const ReclamationBook = ({ uiNavigationVersion, reclamationBookConfig }) => {
  const imageUrl = `https://http2.mlstatic.com/frontend-assets/frontend-navigations/ui-navigation/${uiNavigationVersion}/mercadopago`;

  return (
    <div className="reclamation-book">
      <Image
        id="reclamation-book-image"
        src={`${imageUrl}/reclamations-book__small.png`}
        alt="libro-reclamaciones"
        aria-label="imagen-libro-reclamaciones"
        className="reclamation-book__image"
        srcSet={`${imageUrl}/reclamations-book__large.png 2x`}
        width={51}
        height={30}
      />
      <p className="reclamation-book__text">
        Accede al
        <a
          href={reclamationBookConfig.link}
          data-track-action="CLICK"
          data-track-id="reclamation-book"
        >
          {reclamationBookConfig.name}.
        </a>
      </p>
    </div>
  );
};

ReclamationBook.propTypes = {
  uiNavigationVersion: PropTypes.string.isRequired,
  reclamationBookConfig: PropTypes.shape({
    link: PropTypes.string.isRequired,
    name: PropTypes.string.isRequired,
  }).isRequired,
};

module.exports = ReclamationBook;
