const { getValue } = require('mp-navigation-config');
const { isHidden } = require('../../../../layout-type-detection');

const footerReclamationBookProps = ({ platform, type, req }) => {
  if (isHidden(type)) return {};

  const uiNavigationVersion = getValue({
    key: 'uiNavigationVersion',
    req,
    platform,
  });
  const reclamationBookConfig = getValue({
    key: 'url.helpClaims',
    req,
    platform,
  });

  return { uiNavigationVersion, reclamationBookConfig };
};

module.exports = footerReclamationBookProps;
