/* eslint-disable security/detect-object-injection */
const React = require('react');
const PropTypes = require('prop-types');
const FooterPlus = require('./plus');
const FooterClean = require('./clean');
const FooterHidden = require('./hidden');
const { types } = require('../../layout-type-detection');

const defaultFooter = FooterPlus;
const availableFooters = {
  [types.PLUS]: FooterPlus,
  [types.CLEAN]: FooterClean,
  [types.HIDDEN]: FooterHidden,
};

const Footer = (props) => {
  const { type } = props;

  const ApplicableFooter = availableFooters[type] || defaultFooter;

  return <ApplicableFooter {...props} />;
};

Footer.propTypes = {
  type: PropTypes.string.isRequired,
};

module.exports = Footer;
