const React = require('react');
const PropTypes = require('prop-types');
const classnames = require('classnames');
const Logo = require('../logo');
const { isHidden } = require('../../../../layout-type-detection');

const namespace = 'nav-header';
const BaseHeader = (props) => {
  const { type, children, logo } = props;
  const className = classnames(namespace, {
    [`${namespace}-${type}`]: !isHidden(type),
  });

  return (
    <header className={className}>
      <div className={`${namespace}-menu-wrapper`}>
        <div className={`${namespace}-menu-container`}>
          <Logo {...logo} />
          { children }
        </div>
      </div>
    </header>
  );
};

BaseHeader.propTypes = {
  children: PropTypes.oneOfType([
    PropTypes.arrayOf(PropTypes.node),
    PropTypes.node,
  ]),
  type: PropTypes.string.isRequired,
  logo: PropTypes.object.isRequired,
};

BaseHeader.defaultProps = {
  children: null,
};

module.exports = BaseHeader;
