const React = require('react');
const PropTypes = require('prop-types');

const Logo = (props) => {
  const { url, label } = props;

  return (
    <a className="nav-logo" href={url}>{label}</a>
  );
};

Logo.propTypes = {
  url: PropTypes.string.isRequired,
  label: PropTypes.string.isRequired,
};

module.exports = Logo;
