const React = require('react');
const ShallowRenderer = require('react-test-renderer/shallow');
const BurgerSwitch = require('.');

describe('Mercado Pago Navigation', () => {
  describe('BurguerSwitch Component', () => {
    it('matches the default snapshot for burger switch', () => {
      const renderer = new ShallowRenderer();
      const component = (
        <BurgerSwitch />
      );
      expect(renderer.render(component)).toMatchSnapshot();
    });

    it('matches the default snapshot for burger switch with Prop isLoggedIn', () => {
      const renderer = new ShallowRenderer();
      const component = (
        <BurgerSwitch isLoggedIn />
      );
      expect(renderer.render(component)).toMatchSnapshot();
    });
  });
});
