const React = require('react');
const PropTypes = require('prop-types');
const Welcome = require('./welcome');
const Navigation = require('../navigation');

const namespace = 'nav-header';
const LoggedInUserMenu = (props) => {
  const { welcome, navigation } = props;

  return (
    <nav className={`${namespace}-logged`}>
      <input type="checkbox" id={`${namespace}-user-switch`} />
      <div className={`${namespace}-user-layer`}>
        <Welcome {...welcome} />
        <Navigation {...navigation} />
      </div>
    </nav>
  );
};

LoggedInUserMenu.propTypes = {
  // eslint-disable-next-line react/forbid-prop-types
  welcome: PropTypes.object.isRequired,
  // eslint-disable-next-line react/forbid-prop-types
  navigation: PropTypes.object.isRequired,
};

module.exports = LoggedInUserMenu;
