const React = require('react');
const ShallowRenderer = require('react-test-renderer/shallow');
const UserAvatar = require('.');

const baseProps = {
  avatar: 'one-image.jpg',
  avatarAlt: 'first name last name',
};

describe('Mercado Pago Navigation', () => {
  describe('UserAvatar Component', () => {
    it('matches the default snapshot for userAvatar', () => {
      const renderer = new ShallowRenderer();
      const component = (
        <UserAvatar
          {...baseProps}
        />
      );
      expect(renderer.render(component)).toMatchSnapshot();
    });

    it('matches the default snapshot for userAvatar with className', () => {
      const renderer = new ShallowRenderer();
      const component = (
        <UserAvatar
          {...baseProps}
          className="test"
        />
      );
      expect(renderer.render(component)).toMatchSnapshot();
    });
  });
});
