/* eslint-disable jsx-a11y/no-noninteractive-element-to-interactive-role */
const React = require('react');
const PropTypes = require('prop-types');
const { DESKTOP } = require('@frontend-navigations/commons/constants/device-types');
const Link = require('../../../../../utils/link');
const UserAvatar = require('../user-avatar');
const BurgerSwitch = require('../../burger-switch');
const LandingNavigationDesktop = require('../../navigation/landing-navigation/desktop');

const Welcome = (props) => {
  const { userAvatar, links, siteId, deviceType, landingNavigation } = props;
  const namespace = 'nav-header-user-welcome';

  return (
    <div
      className={`${namespace}`}
    >
      {deviceType === DESKTOP && <LandingNavigationDesktop {...landingNavigation} />}
      <div
        id="nav-header-user-trigger-avatar"
        className={`${namespace}__container-avatar`}
      >
        <UserAvatar {...userAvatar} className={`${namespace}__avatar`} />
      </div>

      <div className={`${namespace}__links`}>
        {
          links.map(link => (
            <Link
              {...link}
              key={link.id}
              className={`${namespace}__link`}
            />
          ))
        }
      </div>

      <BurgerSwitch siteId={siteId} isLoggedIn />
    </div>
  );
};

Welcome.propTypes = {
  // eslint-disable-next-line react/forbid-prop-types
  links: PropTypes.array.isRequired,
  siteId: PropTypes.string.isRequired,
  userAvatar: PropTypes.shape({}).isRequired,
  deviceType: PropTypes.string.isRequired,
  landingNavigation: PropTypes.shape({}),
};

Welcome.defaultProps = {
  landingNavigation: {},
};

module.exports = Welcome;
