/* eslint-disable jsx-a11y/label-has-for */
/* eslint-disable jsx-a11y/label-has-associated-control */
const React = require('react');
const PropTypes = require('prop-types');
const ChevronIcon = require('../../../../../../../icons/chevron');

const CollapsibleItem = ({ id, labelState }) => (
  <label className="ln-collapsible-item" htmlFor={`ln-collapsible-item-${id}`}>
    <p className="ln-collapsible-item__text--open">{labelState.open}</p>
    <p className="ln-collapsible-item__text--closed">{labelState.closed}</p>
    <ChevronIcon direction="down" />
  </label>
);

CollapsibleItem.propTypes = {
  id: PropTypes.string.isRequired,
  labelState: PropTypes.shape({
    open: PropTypes.string,
    closed: PropTypes.string,
  }).isRequired,
};

module.exports = CollapsibleItem;
