const React = require('react');
const PropTypes = require('prop-types');
const ListHeader = require('./list-header');
const ListBody = require('./list-body');

const ListContent = ({ id, header, listItems, texts }) => (
  <>
    <ListHeader id={id} {...header} />
    <hr className="ln-list-content__separator" />
    <ListBody id={id} listItems={listItems} texts={texts} />
  </>
);

ListContent.propTypes = {
  id: PropTypes.string.isRequired,
  header: PropTypes.shape({
    label: PropTypes.string.isRequired,
    sub_label: PropTypes.string.isRequired,
    url: PropTypes.string.isRequired,
    icon: PropTypes.string.isRequired,
  }).isRequired,
  listItems: PropTypes.arrayOf(
    PropTypes.shape({
      id: PropTypes.string,
      url: PropTypes.string,
      icon: PropTypes.string,
      label: PropTypes.string,
    }),
  ).isRequired,
  texts: PropTypes.shape({
    see_less: PropTypes.string,
    see_more: PropTypes.string,
  }).isRequired,
};

module.exports = ListContent;
