const React = require('react');
const PropTypes = require('prop-types');
const tracking = require('../../../../../../../../constants/tracking');
const ChevronIcon = require('../../../../../../../icons/chevron');

const ListHeader = ({ id, label, sub_label: subLabel, url, icon }) => (
  <a
    className="ln-list-header"
    href={url}
    data-track-action={tracking.ACTION}
    data-track-id={id.replace(/_/g, '-')}
    data-track-hierarchy="header"
  >
    <div className="ln-list-header__info">
      <span className={`ln-list-header__icon ln-icon ${icon}`} />
      <p className="ln-list-header__texts">
        <b className="ln-list-header__title">{label}</b>
        <span className="ln-list-header__subtitle">{subLabel}</span>
      </p>
    </div>
    <ChevronIcon direction="right" />
  </a>
);

ListHeader.propTypes = {
  id: PropTypes.string.isRequired,
  label: PropTypes.string.isRequired,
  sub_label: PropTypes.string.isRequired,
  url: PropTypes.string.isRequired,
  icon: PropTypes.string.isRequired,
};

module.exports = ListHeader;
