const React = require('react');
const PropTypes = require('prop-types');
const tracking = require('../../../../../../../../constants/tracking');
const ChevronIcon = require('../../../../../../../icons/chevron');

const ListItem = ({ id, url, iconClass, label }) => (
  <li className="ln-list-item" id={id}>
    <a
      className="ln-list-item__link"
      href={url}
      data-track-action={tracking.ACTION}
      data-track-id={id.replace(/_/g, '-')}
      data-track-hierarchy="l1"
    >
      <p className="ln-list-item__content">
        <span className={`ln-list-item__icon ln-icon ${iconClass}`} />
        {label}
      </p>
      <ChevronIcon direction="right" />
    </a>
  </li>
);

ListItem.propTypes = {
  id: PropTypes.string.isRequired,
  url: PropTypes.string.isRequired,
  iconClass: PropTypes.string.isRequired,
  label: PropTypes.string.isRequired,
};

module.exports = ListItem;
