const React = require('react');
const ShallowRenderer = require('react-test-renderer/shallow');
const ListItem = require('.');

const props = {
  id: 'investments',
  url: 'https://www.mercadopago.com.ar/cuenta#account',
  iconClass: 'digital_account__investments',
  label: 'Generar rendimientos',
};

describe('Mercado Pago Landing Navigation', () => {
  describe('ListItem Component', () => {
    it('Should match with the default snapshot for list-item', () => {
      const renderer = new ShallowRenderer();
      const wrapper = (
        <ListItem {...props} />
      );
      expect(renderer.render(wrapper)).toMatchSnapshot();
    });
  });
});
