const React = require('react');
const PropTypes = require('prop-types');
const NavLink = require('./nav-link');

/* eslint-disable react/no-array-index-key */
const NavContainer = ({ items }) => (
  <nav className="ln-nav-container">
    <ul className="ln-nav-container__list">
      {items.map((item, index) => (<NavLink key={`navlink-key-${index}`} id={item.id} text={item.text} />))}
    </ul>
  </nav>
);

NavContainer.propTypes = {
  items: PropTypes.arrayOf(
    PropTypes.shape({
      id: PropTypes.string,
      text: PropTypes.string,
    }),
  ).isRequired,
};

module.exports = NavContainer;
