const { getValue } = require('mp-navigation-config');

const landingNavigationProps = (props) => {
  const {
    platform,
    req,
    queryParams,
  } = props;
  const { siteId } = platform;

  const landingNavigation = getValue({
    key: 'navHeader.landingNavigation',
    platform,
    req,
  });

  const {
    structure,
    translations,
    urls,
    collapsibleItemLabel,
    enabled,
  } = landingNavigation;

  return {
    siteId,
    structure,
    translations,
    urls,
    collapsibleItemLabel,
    enabled,
    queryParams,
  };
};

module.exports = landingNavigationProps;
