/* eslint-disable jsx-a11y/no-redundant-roles */
const React = require('react');
const PropTypes = require('prop-types');
const UserAvatar = require('../../logged-in/user-avatar');

const UserInfo = (props) => {
  const { firstName, greeting, redirect, brandTitle, userAvatar } = props;
  const namespace = 'nav-header-user-info';

  return (
    <div className={namespace}>
      <UserAvatar {...userAvatar} />
      <div className={`${namespace}__container`}>
        <p className={`${namespace}__greeting`}>{greeting} {firstName}</p>
        <a className={`${namespace}__brand-link`} href={redirect ? redirect.link : null}>
          <b className={`${namespace}__brand-title`}>{brandTitle}</b>
          <span className={`${namespace}__action`}>
            <i className={`${namespace}__icon`} />
          </span>
        </a>
      </div>
    </div>
  );
};

UserInfo.propTypes = {
  // eslint-disable-next-line react/forbid-prop-types
  firstName: PropTypes.string,
  greeting: PropTypes.string,
  brandTitle: PropTypes.string,
  redirect: PropTypes.shape({}),
  userAvatar: PropTypes.shape({
    greeting: PropTypes.string,
    brandTitle: PropTypes.string,
    redirect: PropTypes.string,
  }),
};

UserInfo.defaultProps = {
  userAvatar: {},
  redirect: {},
  firstName: '',
  greeting: '',
  brandTitle: '',
};

module.exports = UserInfo;
