const isUserLoggedIn = require('@frontend-navigations/commons/is-user-logged-in');
const guestMenuProps = require('./guest/props');
const loggedInMenuProps = require('./logged-in/props');

const userMenuProps = (props) => {
  const isLoggedIn = isUserLoggedIn(props);

  return {
    isLoggedIn,
    guestMenu: !isLoggedIn ? guestMenuProps(props) : {},
    loggedInMenu: isLoggedIn ? loggedInMenuProps(props) : {},
  };
};

module.exports = userMenuProps;
