const React = require('react');
const PropTypes = require('prop-types');
const Header = require('../components/base-header');
const UserMenu = require('../components/user-menu');
const { types } = require('../../../layout-type-detection');

const HeaderPlus = ({ userMenu, ...props }) => {
  const plusType = types.PLUS;

  return (
    <Header
      type={plusType}
      {...props}
    >
      <UserMenu {...userMenu} />
    </Header>
  );
};

HeaderPlus.propTypes = {
  // eslint-disable-next-line react/forbid-prop-types
  userMenu: PropTypes.object.isRequired,
};

module.exports = HeaderPlus;
