const logoProps = require('./components/logo/props');
const userMenuProps = require('./components/user-menu/props');

const headerProps = (props) => {
  const { type } = props;

  // Seems like there were some cases for MP where type is passed in upper case, or at least that is
  // what the old code from frontend-layout said. For now we will keep doing the same logic and evaluate
  // why this was added and if we can remove it.
  return {
    type: type && type.toLowerCase(),
    logo: logoProps(props),
    userMenu: userMenuProps(props),
  };
};

module.exports = headerProps;
