/* eslint-disable jsx-a11y/anchor-is-valid */
const getDataBySite = require('./data-management');

const accessKey = 'some-access';

const defaultStructure = {
  [accessKey]: {
    items: [],
    header: null,
  },
};
const defaultTranslations = {
  [accessKey]: {
    label: 'some-translation',
  },
};
const defaultUrls = {};
const defaultQueryParams = {};

describe('Mercado Pago Navigation', () => {
  describe('Data Management', () => {
    it('matches the default snapshot for link with base props', () => {
      const result = getDataBySite(
        true,
        defaultStructure,
        defaultTranslations,
        defaultUrls,
        defaultQueryParams,
      );
      expect(result).toEqual({
        'some-access': {
          label: 'some-translation',
        },
      });
    });
  });
});
