const { MOBILE } = require('@frontend-navigations/commons/constants/device-types');

/**
 * Return the item list that must be used based on the deviceType and linkType.
 *
 * @param {Object} navHeaderConfig - The navHeader config object
 * @param {String} deviceType - The request's device type
 * @param {String} linkType - The type of the link
 */
const getNavHeaderItems = (navHeaderConfig, deviceType, linkType) => (
  deviceType === MOBILE
    ? navHeaderConfig.mobileItemsLinks[linkType]
    : navHeaderConfig.itemsLinks[linkType]
);

module.exports = getNavHeaderItems;
