/* eslint-disable jsx-a11y/anchor-is-valid */
const React = require('react');
const ShallowRenderer = require('react-test-renderer/shallow');
const Link = require('./link');

describe('Mercado Pago Navigation', () => {
  describe('Link Component', () => {
    it('matches the default snapshot for link with base props', () => {
      const renderer = new ShallowRenderer();
      const component = (
        <Link
          link="https://www.mercadopago.com.ar"
          name="Mercado Pago"
        />
      );
      expect(renderer.render(component)).toMatchSnapshot();
    });

    it('matches the default snapshot for link with base props and icon', () => {
      const renderer = new ShallowRenderer();
      const component = (
        <Link
          link="https://www.mercadopago.com.ar"
          name="Mercado Pago"
          showIcon
          icon="help"
        />
      );
      expect(renderer.render(component)).toMatchSnapshot();
    });
  });
});
