/* eslint-disable security/detect-object-injection */
const mapLinksToUrls = ({ urls, items }) => {
  const links = [];

  items.forEach((item) => {
    const { key, name, icon, isBreak } = item;

    if (urls[key]) {
      links.push({
        ...urls[key],
        ...item,
        id: key,
        showIcon: !!(!name && icon),
      });
    } else if (isBreak) {
      links.push(item);
    }
  });

  return links;
};

module.exports = mapLinksToUrls;
