const router = require('nordic/ragnar').router();
const { layoutMiddleware } = require('nordic/layout');
const demoRoute = require('../pages/demo');
const navigation = require('../../../src');
const mshopsMock = require('./shops-mock.json');

router.use((req, res, next) => {
  res.shopModel = mshopsMock;
  next();
});

router.use(layoutMiddleware({ navigation }));
router.get('/', demoRoute);

module.exports = router;
