const {
  APPLICATION,
  FURY,
  PRODUCTION,
} = require('frontend-env');
const { exampleAppName } = require('../constants/environment')

const isDevelopment = !PRODUCTION
  && !FURY;

const isExampleApp = (appName) => {
  const appNameWithDefault = appName || exampleAppName;
  return isDevelopment && APPLICATION === appNameWithDefault;
};

module.exports = {
  isExampleApp,
};
