/* eslint-disable nordic/i18n-jsx-no-literals */

const React = require('react');
const PropTypes = require('prop-types');

const Footer = (props) => {
  const { hide, raw } = props;

  if (hide) {
    return null;
  }

  return <div itemType="http://schema.org/Store" itemID="#this" itemScope dangerouslySetInnerHTML={{ __html: raw }} />;
};

Footer.propTypes = {
  raw: PropTypes.string.isRequired,
  hide: PropTypes.bool,
};

Footer.defaultProps = {
  hide: false,
};

module.exports = Footer;
