const { escape } = require('html-escaper');

function addCartTemplate(attr) {
  return `
    <div class="nav-cart-layer">
      <a href="${attr.link}">
        <img src="${attr.item.image}" class="nav-cart-layer__img" />
        <div class="nav-cart-layer__text-container">
          <span class="nav-cart-layer__legend">${escape(attr.legend)}</span>
          <p class="nav-cart-layer__title">${escape(attr.item.title)}</p>
        </div>
      </a>
    </div>
  `;
}

module.exports = addCartTemplate;
