/**
 * Build the CDN'ized url of some asset
 *
 * @param {string} publicPath A public path that includes domain and URI
 * @param {string} file A filename
 * @returns {string} Final asset's url
 */
function defaultStaticAssetUrlBuilder(publicPath, file) {
  return `${publicPath.replace(/\/$/, '')}/${file}`;
}

module.exports = defaultStaticAssetUrlBuilder;
