/* eslint-disable class-methods-use-this */
const logger = require('../logger');

class NoUniversalAnalyticsManager {
  constructor() {
    logger.warn('UniversalAnalytics needs at least appName property to work. An instance of NoUniversalAnalyticsManager will be created for safe mode.');
  }

  trackEvent() {
    logger.warn('NoUniversalAnalytics: the events will not be sent because an instance of UniversalAnalyticsManager could not be created');
    return false;
  }
}

module.exports = NoUniversalAnalyticsManager;
