const { trackMetric } = require('../collector');
const { supportsNavigatorApi } = require('../support');
const { RAM_SIZE } = require('../../constants/metrics');

const deviceInformationMonitor = () => {
  if (supportsNavigatorApi()) {
    if (window.navigator.deviceMemory) {
      trackMetric(RAM_SIZE, `${window.navigator.deviceMemory}`);
    }
    if (window.navigator.hardwareConcurrency) {
      trackMetric('cpu-cores', `${window.navigator.hardwareConcurrency}`);
    }
  }
};

module.exports = deviceInformationMonitor;
