const arrayOperator = (chunks) => chunks.reduce((acc, chunk) => acc.concat(chunk), []);

const objectOperator = (chunks) => Object.keys(chunks).reduce((acc, key) => {
  const data = chunks[key];
  const chunk = typeof data === 'object' && !Array.isArray(data) && data !== null ? data : { [key]: data };
  return Object.assign(acc, chunk);
}, {});

module.exports = { arrayOperator, objectOperator };
