const {
  get,
  getValue,
  addUpdateHandler,
} = require('../index');

/* eslint-disable no-console */

const gePIonfigurations = () => {
  console.log('get global uiNavigationVersion', get('uiNavigationVersion'));

  console.log('get PI uiNavigationVersion', get('uiNavigationVersion', 'PI'));

  console.log('get PI MLC uiNavigationVersion', get('uiNavigationVersion', 'PI', 'MLC'));

  console.log('getValue global uiNavigationVersion', getValue({
    key: 'uiNavigationVersion',
  }));

  console.log('getValue PI uiNavigationVersion', getValue({
    key: 'uiNavigationVersion',
    platform: {
      id: 'PI',
    },
  }));

  console.log('getValue PI MLC uiNavigationVersion', getValue({
    key: 'uiNavigationVersion',
    platform: {
      id: 'PI',
      siteId: 'MLC',
    },
  }));
};

gePIonfigurations();

addUpdateHandler(() => {
  console.log('Configuration updated');
  gePIonfigurations();
});
