const detection = require('./index');

describe('Layout Type Detection', () => {
  it('returns true when the layout is plus', () => {
    expect(detection.isPlus('full')).toEqual(true);
  });

  it('returns true when the layout is hidden', () => {
    expect(detection.isHidden('hidden')).toEqual(true);
  });

  it('returns false when the layout is plus', () => {
    expect(detection.isHidden('full')).toEqual(false);
  });

  it('returns true when the layout is lite', () => {
    expect(detection.isLite('lite')).toEqual(true);
  });

  it('returns false when the layout is plus', () => {
    expect(detection.isLite('full')).toEqual(false);
  });

  it('returns true when the layout is internal', () => {
    expect(detection.isInternal('internal')).toEqual(true);
  });

  it('returns false when the layout is internal', () => {
    expect(detection.isInternal('full')).toEqual(false);
  });
});
