const { getValue } = require('pi-navigation-config');

const propsMapper = (props) => {
  const uiNavigationVersion = getValue({
    key: 'uiNavigationVersion',
    req: props.req,
    platform: props.platform,
  });

  return {
    platformId: (props.platform || {}).id,
    siteId: (props.platform || {}).siteId,
    cdn: props.cdn,
    favicons: props.favicons,
    uiNavigationVersion,
    appName: 'pi-web-navigation',
  };
};

module.exports = propsMapper;
