const React = require('react');
const PropTypes = require('prop-types');

const HoneypathTrap = ({
  url,
  webView,
  platformId,
  siteId,
  deviceType,
  label,
}) => {
  const enableComponent = url && !webView;

  return enableComponent
    ? (
      <a
        className="nav-footer-hp"
        href={`${url}?p=${platformId}&s=${siteId}&d=${deviceType}`}
      >
        {label}
      </a>
    )
    : null;
};

HoneypathTrap.propTypes = {
  label: PropTypes.string,
  url: PropTypes.string,
  webView: PropTypes.bool,
  platformId: PropTypes.string,
  siteId: PropTypes.string,
  deviceType: PropTypes.string,
};

HoneypathTrap.defaultProps = {
  label: '',
  url: null,
  webView: false,
  platformId: '',
  siteId: '',
  deviceType: '',
};

module.exports = HoneypathTrap;
