const honeypathTrapProps = (props) => {
  const { webView, type: deviceType } = props.device;
  const { id: platformId, siteId } = props.platform;
  const { honeyPath } = props.urls;
  const { name, link } = honeyPath || {};

  return {
    label: name,
    url: link,
    webView,
    platformId,
    siteId,
    deviceType,
  };
};

module.exports = honeypathTrapProps;
