const React = require('react');
const PropTypes = require('prop-types');

const namespace = 'nav-footer';

const buildLinks = (links) => (
  links.map(link => (
    <a key={`link-key-${link.id}`} className={`${namespace}-${link.id}`} href={link.link.href} rel="nofollow">
      {link.name}
    </a>
  ))
);

const GuestMenu = (props) => {
  const { links, secondaryLinks } = props;
  return (
    <>
      <p className={`${namespace}-user`}>
        {buildLinks(links)}
      </p>
      { secondaryLinks && (
        <p className={`${namespace}-secondary-user`}>
          {buildLinks(secondaryLinks)}
        </p>
      )}
    </>
  );
};

GuestMenu.propTypes = {
  links: PropTypes.array.isRequired,
  secondaryLinks: PropTypes.array,
};

GuestMenu.defaultProps = {
  secondaryLinks: null,
};

module.exports = GuestMenu;
