const React = require('react');
const PropTypes = require('prop-types');
const GuestUserMenu = require('./guest');
const LoggedInUserMenu = require('./logged-in');

const UserMenu = (props) => {
  const { isUserLoggedIn, guestMenu, loggedInMenu } = props;

  return isUserLoggedIn ? <LoggedInUserMenu {...loggedInMenu} /> : <GuestUserMenu {...guestMenu} />;
};

UserMenu.propTypes = {
  guestMenu: PropTypes.object,
  loggedInMenu: PropTypes.object,
  isUserLoggedIn: PropTypes.bool.isRequired,
};

UserMenu.defaultProps = {
  guestMenu: null,
  loggedInMenu: null,
};

module.exports = UserMenu;
