/* eslint-disable react/forbid-prop-types */
const React = require('react');
const PropTypes = require('prop-types');
const FooterMobile = require('./mobile');
const FooterDesktop = require('./desktop');

const FooterPlus = (props) => {
  const { isDesktop } = props;

  return (
    isDesktop ? <FooterDesktop {...props} /> : <FooterMobile {...props} />
  );
};

FooterPlus.propTypes = {
  isDesktop: PropTypes.bool.isRequired,
};

module.exports = FooterPlus;
