const React = require('react');
const PropTypes = require('prop-types');

const Internal = (props) => {
  const { title, btnHref, btnText } = props;

  return (
    <>
      { title && <h1 className="nav-title">{ title }</h1> }
      { btnHref
        && (
          <a
            href={btnHref}
            rel="nofollow"
            className="nav-header-btn"
          >
            {btnText}
          </a>
        )}
    </>
  );
};

Internal.propTypes = {
  title: PropTypes.string,
  btnHref: PropTypes.string,
  btnText: PropTypes.string,
};

Internal.defaultProps = {
  title: null,
  btnHref: null,
  btnText: null,
};

module.exports = Internal;
