const { mapLinksToUrls } = require('../../../../../../utils/links');

// TODO: move to conf
const fallbackMenuLinks = [
  'myAccount',
  'logout',
];

const fallbackMenuProps = (props) => {
  const { urls, actualLocation, overrides } = props;

  const links = mapLinksToUrls({
    links: (overrides && overrides.userMenuDesktopLoggedInFallbackLinks) || fallbackMenuLinks,
    urls,
    currentUrl: actualLocation,
    originalProps: props,
    hash: 'nav-header',
  });

  return {
    links,
  };
};

module.exports = fallbackMenuProps;
