const React = require('react');
const PropTypes = require('prop-types');
const UserWidget = require('../../../../../widgets/user-menu');
const Welcome = require('./welcome');
const UserLinks = require('../user-links');

const LoggedInMenuDesktop = (props) => {
  const {
    welcome,
    userLinks,
    userWidget,
  } = props;

  return (
    <>
      <Welcome {...welcome} />
      <UserLinks {...userLinks} />
      <UserWidget {...userWidget} />
    </>
  );
};

LoggedInMenuDesktop.propTypes = {
  welcome: PropTypes.object.isRequired,
  userLinks: PropTypes.object.isRequired,
  userWidget: PropTypes.object.isRequired,
};

module.exports = LoggedInMenuDesktop;
